mapper.select:
SELECT Z1.JISA_CODE AS jisaCode, Z1.BRANCH_CODE AS branchCode, Z1.CORNER_CODE AS cornerCode, Z1.TERMINAL_NO AS terminalNo, Z1.ERROR_DATETIME AS errorDatetime, Z1.CALLEE_REQ_DATETIME AS calleeReqDatetime, Z1.CALLEE_PLAN_DATETIME AS calleePlanDatetime, Z1.ARRIVAL_PLAN_DATETIME AS arrivalPlanDatetime, Z1.CORNER_ARRIVAL_DATETIME AS cornerArrivalDatetime, Z1.HANDLE_DATETIME AS handleDatetime, Z1.CANCLE_REQ_DATETIME AS cancleReqDatetime, Z1.ELAPSED_TIME AS elapsedTime, Z1.CALLEE_REQ_SEQ_NO AS calleeReqSeqNo, Z1.TX_ID AS txId, Z1.ERROR_TYPE AS errorType, Z1.ERROR_PROCESS_STATUS AS errorProcessStatus, Z1.CASH_10K_EMPTY_STATUS AS cash10kEmptyStatus, Z1.CASH_50K_EMPTY_STATUS AS cash50kEmptyStatus, Z1.CALLEE_GUBUN AS calleeGubun, Z2.NOTICE_CONTENT AS noticeContent, Z2.CUSTOMER_INFO AS customerInfo, Z2.HANDLE_CONTENT AS handleContent, Z2.LAST_MODIFY_DATETIME AS lastModifyDatetime, Z2.LAST_MODIFY_EMP_NAME AS lastModifyEmpName, Z3.STEXT_GUBUN AS stextGubun, Z3.TOTAL_CLASSIFY_CODE AS totalClassifyCode FROM ATMS_ERROR_STATUS Z1, ATMS_ERROR_HANDLE_MNG Z2, ATMS_01001110 Z3 WHERE Z1.BRANCH_CODE= Z2.BRANCH_CODE(+) AND Z1.CORNER_CODE = Z2.CORNER_CODE(+) AND Z1.TERMINAL_NO = Z2.TERMINAL_NO(+) AND Z1.ERROR_DATETIME = Z2.ERROR_DATETIME(+) AND Z1.BRANCH_CODE= Z3.BRANCH_CODE AND Z1.CORNER_CODE = Z3.CORNER_CODE AND Z1.TERMINAL_NO = Z3.TERMINAL_NO AND Z1.ERROR_DATETIME = Z3.ERROR_DATETIME AND Z1.BRANCH_CODE = #{branchCode} AND Z1.CORNER_CODE = #{cornerCode} AND Z1.TERMINAL_NO = #{terminalNo}
AND Z1.ERROR_DATETIME BETWEEN #{startDate} AND #{endDate}
ORDER BY Z1.ERROR_DATETIME DESC
